import React from 'react';
import { useQuery } from '@apollo/client';
import { GET_ALL_COMPTES } from '../graphql/queries';

const Comptes = () => {
  const { loading, error, data } = useQuery(GET_ALL_COMPTES);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>Erreur: {error.message}</p>;

  return (
    <div className="comptes-cards">
      {data.allComptes.map((compte) => (
        <div key={compte.id} className="compte-card">
          <h4>Compte ID: {compte.id}</h4>
          <p>Solde: {compte.solde}€</p>
          <p>Date Création: {compte.dateCreation}</p>
          <p>Type: {compte.type}</p>
        </div>
      ))}
    </div>
  );
};

export default Comptes;
