import React, { useState } from 'react';
import AddCompte from '../components/AddComptes';
import Comptes from '../components/Comptes';

const ComptesPage = () => {
  const [showAddCompte, setShowAddCompte] = useState(false);

  return (
    <div className="comptes-page">
      <h2 className="section-title">Liste des Comptes</h2>
      <Comptes />
      <div className="button-group">
        <button
          className="action-button"
          onClick={() => setShowAddCompte(!showAddCompte)}
        >
          {showAddCompte ? 'Masquer Ajouter Compte' : 'Gérer les Comptes'}
        </button>
        <button className="action-button">Total des Soldes</button>
        <button className="action-button">Rechercher un Compte par ID</button>
      </div>
      {showAddCompte && <AddCompte />}
    </div>
  );
};

export default ComptesPage;
