import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { GET_ALL_COMPTES, GET_TOTAL_SOLDE, GET_COMPTE_BY_ID } from '../graphql/queries';
import './style.css';

const Comptes = () => {
  const { loading: loadingComptes, error: errorComptes, data: dataComptes } = useQuery(GET_ALL_COMPTES);
  const [compteId, setCompteId] = useState('');

  const handleSearchChange = (event) => {
    setCompteId(event.target.value);
  };

  const handleSearchClick = (event) => {
    event.preventDefault();
  };

  if (loadingComptes) return <p>Chargement...</p>;
  if (errorComptes) return <p>Erreur: {errorComptes.message}</p>;

  return (
    <div className="comptes-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Chercher un compte par ID"
          value={compteId}
          onChange={handleSearchChange}
        />
        <button onClick={handleSearchClick}>Chercher</button>
      </div>

      <div className="comptes-cards">
        {dataComptes.allComptes.map((compte) => (
          <div className="compte-card" key={compte.id}>
            <h4>Compte ID: {compte.id}</h4>
            <p><strong>Solde:</strong> {compte.solde}€</p>
            <p><strong>Date de création:</strong> {compte.dateCreation}</p>
            <p><strong>Type:</strong> {compte.type}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Comptes;
