import React, { useState } from 'react';
import AddCompte from '../components/AddComptes';
import Comptes from '../components/Comptes';
import './App.css'; // Importing the styles

const ComptesPage = () => {
  const [showAddCompte, setShowAddCompte] = useState(false);

  return (
    <div className="comptes-page">
      <header className="comptes-header">
        <h2 className="section-title">Comptes</h2>
        <div className="search-section">
          <input type="text" placeholder="Chercher un compte" className="search-input" />
          <button className="search-btn">Chercher</button>
        </div>
        <button 
          className="add-btn" 
          onClick={() => setShowAddCompte(!showAddCompte)}
        >
          {showAddCompte ? 'Annuler' : 'Ajouter un compte'}
        </button>
      </header>

      {showAddCompte && <AddCompte />}
      <Comptes />
    </div>
  );
};

export default ComptesPage;
